package com.ctfs.BRB.TestCase;

import java.io.BufferedInputStream;
import java.io.ByteArrayInputStream;
import java.io.DataInputStream;
import org.apache.commons.codec.binary.Base64;
import org.junit.Assert;
import org.junit.Test;

import com.ctfs.BRB.Helper.ImageUtils;

public class ImageUtilsTest
{

	String inputStrDataUrl = "data:image/png;base64,";

	@SuppressWarnings("deprecation")
	@Test
	public void test_that_converted_image_is_base64encoded() throws Exception
	{
		ImageUtils sut = new ImageUtils();
		byte[] convertedImageExpectedtoBeBase64Encoded = sut.convertPNGDataURLToJPGByteArray(inputStrDataUrl);
		Assert.assertTrue(Base64.isArrayByteBase64(convertedImageExpectedtoBeBase64Encoded));
	}

	@Test
	public void test_that_converted_image_is_in_jpeg_format() throws Exception
	{
		ImageUtils sut = new ImageUtils();
		byte[] convertedImage = sut.convertPNGDataURLToJPGByteArray(inputStrDataUrl);
		byte[] decodedJPG = Base64.decodeBase64(convertedImage);

		ByteArrayInputStream byteArrayStreamForDecodedJPGImage = new ByteArrayInputStream(decodedJPG);
		DataInputStream jpegImageInputStream = new DataInputStream(new BufferedInputStream(byteArrayStreamForDecodedJPGImage));

		// This is the JPEG header
		Assert.assertTrue(jpegImageInputStream.readInt() == 0xffd8ffe0);

		jpegImageInputStream.close();
	}
}
